import React, { useState, useCallback } from "react";
import { GoogleMap, useJsApiLoader, OverlayView } from "@react-google-maps/api";
import { Restaurant } from "@mui/icons-material";
import { Box, Typography } from "@mui/material";
import { styled } from "@mui/system";

const MarkerContainer = styled("div")({
  display: "flex",
  flexDirection: "column",
  alignItems: "center",
  textAlign: "center",
  background: "gray",
  border: "1px solid #ccc",
  padding: "5px",
  borderRadius: "5px",
  cursor: "pointer",
  width: "max-content",
});

const MarkerIcon = styled(Restaurant)({
  fontSize: 24,
  color: "#Fff", // Customize the color of the icon
});

const Map = ({ setCoordinates, setBounds, coordinates, places }) => {
  const { isLoaded } = useJsApiLoader({
    id: "google-map-script",
    googleMapsApiKey: "AIzaSyBCL1EwLHgn8s3qXIk-rTVtr3DW3P8ZHR4", // Replace with your API key
  });

  const [map, setMap] = useState(null);

  const onLoad = useCallback((map) => {
    setMap(map);
  }, []);

  const onUnmount = useCallback(() => {
    setMap(null);
  }, []);

  const handleBoundsChanged = useCallback(() => {
    if (map) {
      const bounds = map.getBounds();
      if (bounds) {
        const ne = bounds.getNorthEast();
        const sw = bounds.getSouthWest();
        setBounds({ northeast: ne.toJSON(), southwest: sw.toJSON() });
      }
    }
  }, [map, setBounds]);

  const handleCenterChanged = useCallback(() => {
    if (map) {
      const center = map.getCenter();
      setCoordinates({ lat: center.lat(), lng: center.lng() });
    }
  }, [map, setCoordinates]);

  return isLoaded ? (
    <GoogleMap
      mapContainerStyle={{ height: "70vh", width: "100%" }}
      center={coordinates}
      zoom={15}
      onLoad={onLoad}
      onUnmount={onUnmount}
      onBoundsChanged={handleBoundsChanged}
      onCenterChanged={handleCenterChanged}
    >
      {places.map((place, index) => (
        <OverlayView
          key={index}
          position={place.geometry.location}
          mapPaneName={OverlayView.OVERLAY_MOUSE_TARGET}
        >
          <MarkerContainer>
            <MarkerIcon />
            <Typography variant="body2">{place.rating}</Typography>
          </MarkerContainer>
        </OverlayView>
      ))}
      <OverlayView
        position={coordinates}
        mapPaneName={OverlayView.OVERLAY_MOUSE_TARGET}
      >
        <MarkerContainer>
          <Typography variant="body2">You are here</Typography>
        </MarkerContainer>
      </OverlayView>
    </GoogleMap>
  ) : (
    <></>
  );
};

export default React.memo(Map);
