import React, { useState, useCallback, useEffect } from "react";
import { GoogleMap, useJsApiLoader, Marker, useMediaQuery } from "@react-google-maps/api";
import { LocationOnOutlined } from "@mui/icons-material";
import { Paper, Typography } from "@mui/material";

const containerStyle = {
  height: "70vh",
  width: "100%",
};
const isDesktop = useMediaQuery('min')
const isMobile = window.innerWidth <= 768; // Example condition for mobile devices

function Map({ setCoordinates, setBounds, coordinates, places }) {
  const { isLoaded } = useJsApiLoader({
    id: "google-map-script",
    googleMapsApiKey: "AIzaSyBCL1EwLHgn8s3qXIk-rTVtr3DW3P8ZHR4", // API key
  });

  const [map, setMap] = useState(null);
  const [mapBounds, setMapBounds] = useState(null);
  const [mapCenter, setMapCenter] = useState(null);

  const onLoad = useCallback((map) => {
    setMap(map);
  }, []);

  const onUnmount = useCallback(() => {
    setMap(null);
  }, []);

  const handleBoundsChanged = () => {
    if (map) {
      const bounds = map.getBounds();
      if (bounds) {
        const ne = bounds.getNorthEast();
        const sw = bounds.getSouthWest();
        const newBounds = {
          ne: { lat: ne.lat(), lng: ne.lng() },
          sw: { lat: sw.lat(), lng: sw.lng() },
        };
        if (JSON.stringify(newBounds) !== JSON.stringify(mapBounds)) {
          setMapBounds(newBounds);
          setBounds(newBounds);
          console.log("Boundsnew:", newBounds);
        }
      }
    }
  };

  const handleCenterChanged = () => {
    if (map) {
      const newCenter = map.getCenter();
      if (newCenter) {
        const newCoordinates = { lat: newCenter.lat(), lng: newCenter.lng() };
        if (JSON.stringify(newCoordinates) !== JSON.stringify(mapCenter)) {
          setMapCenter(newCoordinates);
          setCoordinates(newCoordinates);
          console.log("Center:", newCoordinates);
        }
      }
    }
  };

  useEffect(() => {
    setBounds(mapBounds);
  }, [mapBounds, setBounds]);

  useEffect(() => {
    setCoordinates(mapCenter);
  }, [mapCenter, setCoordinates]);

  return isLoaded ? (
    <GoogleMap
      mapContainerStyle={containerStyle}
      center={coordinates}
      zoom={15}
      onLoad={onLoad}
      onUnmount={onUnmount}
      onCenterChanged={handleCenterChanged}
      onBoundsChanged={handleBoundsChanged}
      onChildClick={""}
    >
      {places?.map((place, i) => (
        <div lat={Number(place.latitute)} lng={Number(place.longitute)} key={i}>
          {isMobile ? (
            <LocationOnOutlined color="primary" fontSize="large" />
          ) : (
            <Paper elevation="3">
              <Typography variant="subtitle2" gutterBottom>
                {place.name}
              </Typography>
              <img
                src={place.photo
    ? place.photo.images.large.url: "/chairs-cutlery-fork.webp"}
                alt={place.name}
              />
            </Paper>
          )}
        </div>
      ))}
      <Marker position={coordinates} />
    </GoogleMap>
  ) : (
    <></>
  );
}

export default React.memo(Map);
