import React, { useEffect, useState } from "react";
import { ThemeProvider, createTheme, styled } from "@mui/material/styles";
import { CssBaseline, Box, Paper } from "@mui/material";
import Header from "./components/Header/Header";
import List from "./components/List/List";
import Map from "./components/Map/Map";
import Grid from "@mui/material/Unstable_Grid2";
import { getPlacesDetails } from "./API";

// Define your theme
const theme = createTheme({
  palette: {
    primary: {
      main: "#007bff", // Adjust color values as needed
    },
    secondary: {
      main: "#28a745", // Adjust color values as needed
    },
  },
  // Other theme options...
});

const Item = styled(Paper)(({ theme }) => ({
  backgroundColor: theme.palette.mode === "dark" ? "#1A2027" : "#fff",
  ...theme.typography.body2,
  padding: theme.spacing(1),
  textAlign: "center",
  color: theme.palette.text.secondary,
}));

function App() {
  const [initialCenter, setInitialCenter] = useState(null);
  const [error, setError] = useState(null);
  const [places, setPlaces] = useState([]);
  const [coordinates, setCoordinates] = useState({ lat: 0, lng: 0 });
  const [bounds, setBounds] = useState(null);

  useEffect(() => {
    // Set initial center to default coordinates
    setInitialCenter({ lat: 0, lng: 0 });

    // Try to retrieve user's current location with high accuracy
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          const { latitude, longitude } = position.coords;
          setInitialCenter({ lat: latitude, lng: longitude });
        },
        (error) => {
          setError(error.message);
        },
        { enableHighAccuracy: true }
      );
    } else {
      setError("Geolocation is not supported by this browser.");
    }
  }, []); // This effect runs only once after the component mounts

  useEffect(() => {
    if (coordinates && bounds) {
      console.log(coordinates, bounds);
      getPlacesDetails().then((data) => {
        setPlaces(data);
      });
    }
  }, [coordinates, bounds]);

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <Header />
      <Box sx={{ flexGrow: 1 }}>
        <Grid container spacing={2}>
          <Grid xs={12} sm={12} md={4}>
            <Item>
              <List />
            </Item>
          </Grid>
          <Grid xs={12} sm={12} md={8}>
            <Item>
              {error && <p>{error}</p>}
              <Map
                initialCenter={initialCenter}
                setCoordinates={setCoordinates}
                setBounds={setBounds}
                coordinates={coordinates}
              />
            </Item>
          </Grid>
        </Grid>
      </Box>
    </ThemeProvider>
  );
}

export default App;
