import axios from "axios";

const API_KEY = "AIzaSyBCL1EwLHgn8s3qXIk-rTVtr3DW3P8ZHR4";
const URL = "https://maps.googleapis.com/maps/api/place/nearbysearch/json";

export const getPlacesDetails = async (sw, ne) => {
  const options = {
    method: "GET",
    url: URL,
    params: {
      key: API_KEY,
      location: `${(sw.lat + ne.lat) / 2},${(sw.lng + ne.lng) / 2}`,
      radius: 15000, // Adjust the radius as needed
      type: "restaurant", // Specify the type of places you're interested in
    },
  };

  try {
    const response = await axios.request(options);
    console.log("Full API Response:", response);
    const { results } = response.data;
    console.log("Data from API:", results);
    return results;
  } catch (error) {
    console.error("Error fetching data from API:", error);
    return null;
  }
};
