import axios from "axios";

const API_KEY = "YOUR_GOOGLE_API_KEY"; // replace with your API key
const URL = "https://maps.googleapis.com/maps/api/place/textsearch/json";

export const getPlacesDetails = async (category, radius, lat, lng) => {
  const requestOptions = {
    method: "GET",
    redirect: "follow",
  };

  const searchURL = `${URL}?query=${category}&location=${lat}%2C${lng}&radius=${radius}&key=${API_KEY}`;

  try {
    const response = await fetch(searchURL, requestOptions);
    const result = await response.json();
    console.log("Full API Response:", result);
    return result.results;
  } catch (error) {
    console.error("Error fetching data from API:", error);
    return null;
  }
};
