import React, { useState, useCallback, useEffect } from "react";
import {
  GoogleMap,
  useJsApiLoader,
  Marker,
  OverlayView,
} from "@react-google-maps/api";
import RestaurantIcon from "@mui/icons-material/Restaurant";

const containerStyle = {
  height: "70vh",
  width: "100%",
};

const getPixelPositionOffset = (offsetWidth, offsetHeight, labelAnchor) => {
  return {
    x: offsetWidth + labelAnchor.x,
    y: offsetHeight + labelAnchor.y,
  };
};

function Map({ places, userLocation }) {
  const { isLoaded } = useJsApiLoader({
    id: "google-map-script",
    googleMapsApiKey: "AIzaSyBCL1EwLHgn8s3qXIk-rTVtr3DW3P8ZHR4",
  });

  const [map, setMap] = useState(null);

  const onLoad = useCallback((map) => {
    setMap(map);
  }, []);

  const onUnmount = useCallback(() => {
    setMap(null);
  }, []);

  useEffect(() => {
    if (map && places.length > 0) {
      const bounds = new window.google.maps.LatLngBounds();
      places.forEach((place) => {
        bounds.extend(place.geometry.location);
      });
      bounds.extend(userLocation); // Include user location in bounds
      map.fitBounds(bounds);
    }
  }, [map, places, userLocation]);

  return isLoaded ? (
    <GoogleMap
      mapContainerStyle={containerStyle}
      center={userLocation}
      zoom={18}
      onLoad={onLoad}
      onUnmount={onUnmount}
    >
      <Marker position={userLocation} />{" "}
      {/* Default marker for user's location */}
      {places.map((place, index) => (
        <React.Fragment key={index}>
          <OverlayView
            position={{
              lat: Number(place.geometry.location.lat),
              lng: Number(place.geometry.location.lng),
            }}
            mapPaneName={OverlayView.OVERLAY_MOUSE_TARGET}
            getPixelPositionOffset={(offsetWidth, offsetHeight) =>
              getPixelPositionOffset(offsetWidth, offsetHeight, {
                x: -30,
                y: -15,
              })
            }
          >
            <div
              style={{
                background: `#203254`,
                padding: `7px 12px`,
                fontSize: "11px",
                color: `white`,
                borderRadius: "4px",
                display: "flex",
                alignItems: "center",
                width: "max-content",
              }}
            >
              <RestaurantIcon style={{ marginRight: 4 }} />
              <span>{place.rating}</span>
            </div>
          </OverlayView>
        </React.Fragment>
      ))}
    </GoogleMap>
  ) : (
    <></>
  );
}

export default React.memo(Map);
