import React, { useEffect, useState } from "react";
import { ThemeProvider, createTheme, styled } from "@mui/material/styles";
import { CssBaseline, Box, Paper } from "@mui/material";
import Header from "./components/Header/Header";
import Grid from "@mui/material/Unstable_Grid2";
import "./App.css";
import axios from "axios";

const theme = createTheme({
  palette: {
    primary: {
      main: "#007bff",
    },
    secondary: {
      main: "#28a745",
    },
  },
});

const Item = styled(Paper)(({ theme }) => ({
  backgroundColor: theme.palette.mode === "dark" ? "#1A2027" : "#fff",
  ...theme.typography.body2,
  padding: theme.spacing(1),
  textAlign: "center",
  color: theme.palette.text.secondary,
}));

function App() {
  const [initialCenter, setInitialCenter] = useState({ lat: 0, lng: 0 });
  const [error, setError] = useState(null);
  //const [places, setPlaces] = useState([]);
  const [coordinates, setCoordinates] = useState({ lat: 0, lng: 0 });

  useEffect(() => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          const { latitude, longitude } = position.coords;
          setInitialCenter({ lat: latitude, lng: longitude });
          setCoordinates({ lat: latitude, lng: longitude });
        },
        (error) => {
          setError(error.message);
        },
        { enableHighAccuracy: true }
      );
    } else {
      setError("Geolocation is not supported by this browser.");
    }
  }, []);
  useEffect(() => {


const BASE_URL = "https://maps.googleapis.com/maps/api/place";
const GOOGLE_API_KEY = process.env.GOOGLE_API_KEY;

const fetchData = async () => {

    const {searchParams}=new URL(request.url)
    const category=searchParams.get('category');
    const radius = searchParams.get("radius");
    const lat = searchParams.get("lat");
    const lng = searchParams.get("lng");
    console.log( BASE_URL + "/textsearch/json?query="+category+ 
    "&location="+lat+","+lng+
    '&radius='+radius+
    "&key=" + GOOGLE_API_KEY)
    const res=await fetch(
        BASE_URL + "/textsearch/json?query="+category+ 
    "&location="+lat+","+lng+
    '&radius='+radius+
    "&key=" + GOOGLE_API_KEY,
    {
      headers: {
        "Content-Type": "application/json",
      },
    }
    );
    const product = await res.json();

    return json({ product });
}

  }, []);

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <Header />
      <Box sx={{ flexGrow: 1 }}>
        <Grid container spacing={2}>
          <Grid xs={12} sm={12} md={4}>
            <Item>{/**  <List places={places} />**/}</Item>
          </Grid>
          <Grid xs={12} sm={12} md={8}>
            <Item>{error && <p>{error}</p>}</Item>
          </Grid>
        </Grid>
      </Box>
    </ThemeProvider>
  );
}

export default App;
