import React, { useState, useCallback, useEffect, useRef } from "react";
import {
  GoogleMap,
  useJsApiLoader,
  Marker,
  InfoWindow,
  OverlayView,
} from "@react-google-maps/api";
import { Star, Restaurant, PersonPinCircleRounded } from "@mui/icons-material";
import { Paper, Typography, useMediaQuery } from "@mui/material";
import { styled } from "@mui/material/styles";

const containerStyle = {
  height: "70vh",
  width: "100%",
};

const StyledPaper = styled(Paper)(() => ({
  padding: "10px",
  display: "flex",
  flexDirection: "column",
  justifyContent: "center",
  width: "max-content",
}));

const mapStyles = [
  {
    elementType: "geometry",
  },
  // Add other map styles as needed
];

function Map({ setCoordinates, setBounds, coordinates, places }) {
  const isDesktop = useMediaQuery("(min-width:600px)");
  const { isLoaded, loadError } = useJsApiLoader({
    id: "google-map-script",
    googleMapsApiKey: "AIzaSyBCL1EwLHgn8s3qXIk-rTVtr3DW3P8ZHR4",
  });

  const [map, setMap] = useState(null);
  const [selectedPlace, setSelectedPlace] = useState(null);

  const mapRef = useRef(null);
  const prevCoordinatesRef = useRef(coordinates);
  const prevBoundsRef = useRef(bounds);

  const onLoad = useCallback((mapInstance) => {
    setMap(mapInstance);
    mapRef.current = mapInstance;
  }, []);

  const onUnmount = useCallback(() => {
    setMap(null);
    mapRef.current = null;
  }, []);

  const handleBoundsChanged = useCallback(() => {
    if (mapRef.current) {
      const bounds = mapRef.current.getBounds();
      if (bounds) {
        const ne = bounds.getNorthEast();
        const sw = bounds.getSouthWest();
        const newBounds = {
          northeast: { lat: ne.lat(), lng: ne.lng() },
          southwest: { lat: sw.lat(), lng: sw.lng() },
        };
        if (
          JSON.stringify(prevBoundsRef.current) !== JSON.stringify(newBounds)
        ) {
          setBounds(newBounds);
          prevBoundsRef.current = newBounds;
        }
      }
    }
  }, [setBounds]);

  const handleCenterChanged = useCallback(() => {
    if (mapRef.current) {
      const newCenter = mapRef.current.getCenter();
      if (newCenter) {
        const newCoordinates = { lat: newCenter.lat(), lng: newCenter.lng() };
        if (
          JSON.stringify(prevCoordinatesRef.current) !==
          JSON.stringify(newCoordinates)
        ) {
          setCoordinates(newCoordinates);
          prevCoordinatesRef.current = newCoordinates;
        }
      }
    }
  }, [setCoordinates]);

  useEffect(() => {
    if (mapRef.current) {
      handleBoundsChanged();
      handleCenterChanged();
    }
  }, [mapRef.current, handleBoundsChanged, handleCenterChanged]);

  useEffect(() => {
    navigator.geolocation.getCurrentPosition(
      (position) => {
        const { latitude, longitude } = position.coords;
        setCoordinates({ lat: latitude, lng: longitude });
        console.log(`User's location: ${latitude}, ${longitude}`);
      },
      (error) => {
        console.error("Error getting user's location: ", error);
      }
    );
  }, [setCoordinates]);

  const handleMarkerClick = useCallback((place) => {
    setSelectedPlace(place);
  }, []);

  console.log("Places:", places); // Log the places array

  if (loadError) {
    return <div>Error loading maps</div>;
  }

  return isLoaded ? (
    <GoogleMap
      mapContainerStyle={containerStyle}
      center={coordinates}
      zoom={15}
      options={{ styles: mapStyles }}
      onLoad={onLoad}
      onUnmount={onUnmount}
      onCenterChanged={handleCenterChanged}
      onBoundsChanged={handleBoundsChanged}
    >
      {places?.map((place) => {
        console.log("Place:", place); // Log each place
        return (
          <Marker
            key={place.place_id}
            position={{
              lat: Number(place.geometry.location.lat),
              lng: Number(place.geometry.location.lng),
            }}
            onClick={() => handleMarkerClick(place)}
            icon={{
              url: "https://maps.gstatic.com/mapfiles/place_api/icons/restaurant-71.png",
              scaledSize: new window.google.maps.Size(25, 25),
            }}
          />
        );
      })}
      {selectedPlace && (
        <InfoWindow
          position={{
            lat: Number(selectedPlace.geometry.location.lat),
            lng: Number(selectedPlace.geometry.location.lng),
          }}
          onCloseClick={() => setSelectedPlace(null)}
        >
          <StyledPaper elevation={3}>
            <img
              src={
                selectedPlace.photos && selectedPlace.photos.length > 0
                  ? `https://maps.googleapis.com/maps/api/place/photo?maxwidth=400&photoreference=${selectedPlace.photos[0].photo_reference}&key=AIzaSyBCL1EwLHgn8s3qXIk-rTVtr3DW3P8ZHR4`
                  : "/chairs-cutlery-fork.webp"
              }
              alt={selectedPlace.name}
              style={{ width: "auto", height: "100px" }}
            />
            <Typography variant="body2" gutterBottom>
              {selectedPlace.name}
            </Typography>
            <Typography variant="body2" display="flex" alignItems="center">
              {selectedPlace.rating}
              <Star style={{ color: "gold", marginLeft: 5 }} />
              <Restaurant style={{ marginLeft: 5 }} />(
              {selectedPlace.user_ratings_total} reviews)
            </Typography>
            <Typography variant="body2">
              Price Level: {selectedPlace.price_level}
            </Typography>
            <Typography variant="body2">
              {selectedPlace.opening_hours?.open_now ? "Open Now" : "Closed"}
            </Typography>
          </StyledPaper>
        </InfoWindow>
      )}
      <OverlayView
        position={coordinates}
        mapPaneName={OverlayView.OVERLAY_MOUSE_TARGET}
      >
        <PersonPinCircleRounded style={{ fontSize: 50, color: "#e8eaed" }} />
      </OverlayView>
    </GoogleMap>
  ) : (
    <div>Loading...</div>
  );
}

export default React.memo(Map);
