import React, { useEffect, useState } from "react";
import { ThemeProvider, createTheme, styled } from "@mui/material/styles";
import { CssBaseline, Box, Paper } from "@mui/material";
import Header from "./components/Header/Header";
// import List from "./components/List/List";
import Map from "./components/Map/Map";
import Grid from "@mui/material/Unstable_Grid2";
import "./App.css";

const theme = createTheme({
  palette: {
    primary: {
      main: "#007bff",
    },
    secondary: {
      main: "#28a745",
    },
  },
});

const Item = styled(Paper)(({ theme }) => ({
  backgroundColor: theme.palette.mode === "dark" ? "#1A2027" : "#fff",
  ...theme.typography.body2,
  padding: theme.spacing(1),
  textAlign: "center",
  color: theme.palette.text.secondary,
}));

function App() {
  const [initialCenter, setInitialCenter] = useState({ lat: 0, lng: 0 });
  const [error, setError] = useState(null);
  const [places, setPlaces] = useState([]);
  const [coordinates, setCoordinates] = useState({ lat: 0, lng: 0 });

  useEffect(() => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          const { latitude, longitude } = position.coords;
          setInitialCenter({ lat: latitude, lng: longitude });
          setCoordinates({ lat: latitude, lng: longitude });
        },
        (error) => {
          setError(error.message);
        },
        { enableHighAccuracy: true }
      );
    } else {
      setError("Geolocation is not supported by this browser.");
    }
  }, []);

  useEffect(() => {
    if (coordinates.lat !== 0 && coordinates.lng !== 0) {
      const requestOptions = {
        method: "GET",
        redirect: "follow",
      };

      const fetchPlaces = async () => {
        try {
          const response = await fetch(
            `https://maps.googleapis.com/maps/api/place/textsearch/json?query=restaurant&location=${coordinates.lat}%2C$,${coordinates.lng}&radius=50000&key=AIzaSyBCL1EwLHgn8s3qXIk-rTVtr3DW3P8ZHR4`,
            requestOptions
          );
          const result = await response.text();
          console.log(result);
        } catch (error) {
          console.error("Error fetching data from API:", error);
        }
      };

      fetchPlaces();
    }
  }, [coordinates]);

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <Header />
      <Box sx={{ flexGrow: 1 }}>
        <Grid container spacing={2}>
          <Grid xs={12} sm={12} md={4}>
            <Item>{/**  <List places={places} />**/}</Item>
          </Grid>
          <Grid xs={12} sm={12} md={8}>
            <Item>
              {error && <p>{error}</p>}
              <Map
                initialCenter={initialCenter}
                setCoordinates={setCoordinates}
                coordinates={coordinates}
                places={places}
              />
            </Item>
          </Grid>
        </Grid>
      </Box>
    </ThemeProvider>
  );
}

export default App;
