export const getPlacesDetails = async (category, radius, lat, lng) => {
    const API_KEY = ""; // replace with your API key
    const URL = `https://maps.googleapis.com/maps/api/place/textsearch/json?query=${encodeURIComponent(category)}&location=${lat},${lng}&radius=${radius}&key=${API_KEY}`;
  
    const requestOptions = {
      method: "GET",
      redirect: "follow",
    };
  
    try {
      const response = await fetch(URL, requestOptions);
      const result = await response.json();
      console.log("Full API Response:", result);
      return result.results;
    } catch (error) {
      console.error("Error fetching data from API:", error);
      return null;
    }
  };
