// Map.js
import React, { useState, useCallback, useEffect } from "react";
import { GoogleMap, useJsApiLoader, Marker } from "@react-google-maps/api";

const containerStyle = {
  height: "70vh",
};

function Map({ setCoordinates, setBounds, coordinates }) {
  const { isLoaded } = useJsApiLoader({
    id: "google-map-script",
    googleMapsApiKey: "AIzaSyBCL1EwLHgn8s3qXIk-rTVtr3DW3P8ZHR4", // Replace with your actual API key
  });

  const [map, setMap] = useState(null);
  const [initialCenter, setInitialCenter] = useState({
    lat: -26.196368068356826,
    lng: 28.032628842080946,
  });

  useEffect(() => {
    // Set initial center to default coordinates
    setInitialCenter({ lat: -26.196368068356826, lng: 28.032628842080946 });

    // Try to retrieve user's current location with high accuracy
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          const { latitude, longitude } = position.coords;
          setInitialCenter({ lat: latitude, lng: longitude });
        },
        (error) => {
          console.error(error);
        },
        { enableHighAccuracy: true }
      );
    } else {
      console.error("Geolocation is not supported by this browser.");
    }
  }, []); // This effect runs only once after the component mounts

  const onLoad = useCallback(
    (map) => {
      setMap(map);
      // Adjust the map bounds based on the initial center
      const bounds = new window.google.maps.LatLngBounds();
      bounds.extend(
        new window.google.maps.LatLng(initialCenter.lat, initialCenter.lng)
      );
      map.fitBounds(bounds);
    },
    [initialCenter]
  );

  const onUnmount = useCallback(() => {
    setMap(null);
  }, []);

  const handleBoundsChanged = () => {
    if (map) {
      const bounds = map.getBounds();
      const ne = bounds.getNorthEast();
      const sw = bounds.getSouthWest();
      const newBounds = {
        ne: { lat: ne.lat(), lng: ne.lng() },
        sw: { lat: sw.lat(), lng: sw.lng() },
      };
      setBounds(newBounds);
      console.log("Bounds:", newBounds);
    }
  };

  const handleCenterChanged = () => {
    if (map) {
      const newCenter = map.getCenter();
      setCoordinates({ lat: newCenter.lat(), lng: newCenter.lng() });
    }
  };

  return isLoaded ? (
    <GoogleMap
      mapContainerStyle={containerStyle}
      center={initialCenter}
      zoom={1}
      onLoad={onLoad}
      onUnmount={onUnmount}
      onCenterChanged={handleCenterChanged}
      onBoundsChanged={handleBoundsChanged}
    >
      {/* Marker for the center */}
      <Marker
        position={{
          lat: initialCenter.lat,
          lng: initialCenter.lng,
        }}
      />
    </GoogleMap>
  ) : (
    <></>
  );
}

export default React.memo(Map);
